-- Створення бази даних
CREATE DATABASE IF NOT EXISTS StudentDB;

-- Використання бази даних StudentDB
USE StudentDB;

-- Створення таблиці students
CREATE TABLE IF NOT EXISTS students (
                                        ID INT PRIMARY KEY AUTO_INCREMENT, -- Унікальний ідентифікатор
                                        LastName VARCHAR(50) NOT NULL,     -- Прізвище
                                        FirstName VARCHAR(50) NOT NULL,    -- Ім'я
                                        MiddleName VARCHAR(50),            -- По батькові
                                        BirthDate DATE NOT NULL,           -- Дата народження
                                        RecordBookNumber VARCHAR(20) NOT NULL
);

-- Додавання даних до таблиці students
